namespace Ecomteck\Bannerslider\Controller\Adminhtml;

/**
 * Slider Abstract Action
 * @category Ecomteck
 * @package  Ecomteck_Bannerslider
 * @module   Bannerslider
 * @author   Ecomteck Developer
 */
abstract class Slider extends \Ecomteck\Bannerslider\Controller\Adminhtml\AbstractAction
{
    const PARAM_CRUD_ID = 'slider_id';

    /**
     * Check if admin has permissions to visit related pages.
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Ecomteck_Bannerslider::bannerslider_sliders');
    }
}
